//// Täglicher Export/ Mail mit CSV Daten der neuen Signups mit Checkout 
//// plus Kennzahlen im Mailtext
function dailyCSVmail(){
    global $con;
    
    // Datenbank abfragen und Ergebnisarray erstellen
    $sql = "SELECT ID, CreatedOn, Timestamp, UsrToken, Email, Name, Age, Sex, Height, Weight, Sitreach, ";
    $sql .= "Rotation, Broadj_Size, Broadj_Steps, Plank_Min, Plank_Sec, Pull, Flexhang, PU, PUmod, 20mSR_Level, 20mSR_Lap, ";
    $sql .= "Recovrate, 16Run_Min, 16Run_Sec, Paystatus FROM fittestresults WHERE Exported = 0 AND Paystatus != ''";
    $result = mysqli_query($con, $sql);

    if(mysqli_num_rows($result) != 0){
        // CSV Datei generieren
        $file = fopen("data.csv", "w");
    
        while($row = mysqli_fetch_row($result)){
            fputcsv($file,$row);
            
            // Vermerk auf Export in der Datenbank setzen.
            $sql = "UPDATE fittestresults SET Exported = 1 WHERE ID = ".$row[0];
            mysqli_query($con, $sql);
        }
        
        fclose($file);
    
        // Email mit Anhang vorbereiten und versenden
        $email_receiver = "josch@sidefits.com, phil@sidefits.com";
        $email_subject = "FitTest Daten-Update";
    
        $email_msg = "Dies ist eine automatisch generierte Email.\n\n Anbei die neuen Daten von FitTest Nutzern, die ihre Ergebnisse abgesendet haben.";
    
        $file_size = filesize("data.csv");
        $handle = fopen("data.csv", "r");
        $content = fread($handle, $file_size);
        fclose($handle);
        $content = chunk_split(base64_encode($content));
        $uid = md5(uniqid(time()));
        $name = basename($file);
        $header = "From: Sidefits FitTest System <fittest@sidefits.com>\r\n";
        $header .= "Reply-To: fittest@sidefits.com\r\n";
        $header .= "MIME-Version: 1.0\r\n";
        $header .= "Content-Type: multipart/mixed; boundary=\"".$uid."\"\r\n\r\n";
        $header .= "This is a multi-part message in MIME format.\r\n";
        $header .= "--".$uid."\r\n";
        $header .= "Content-type:text/plain; charset=utf-8\r\n";
        $header .= "Content-Transfer-Encoding: 7bit\r\n\r\n";
        $header .= $email_msg."\r\n\r\n";
        $header .= "--".$uid."\r\n";
        $header .= "Content-Type: application/octet-stream; name=\"fittest_data.csv\"\r\n"; // use different content types here
        $header .= "Content-Transfer-Encoding: base64\r\n";
        $header .= "Content-Disposition: attachment; filename=\"fittest_data.csv\"\r\n\r\n";
        $header .= $content."\r\n\r\n";
        $header .= "--".$uid."--";
    
        mail($email_receiver, $email_subject, "", $header);
    
        mysqli_free_result($result);    
    }    
}                                    

