//// Erinnerungsmail nach X Wochen nach übersendeter Auswertung
function reminderXweeks ( $lang, $email, $name, $token, $weeks ){
    global $con;

    // Email header vorbereiten
    $headers = "From: Sidefits FitTest <fittest@sidefits.com>\r\n";
    $headers .= "Reply-To: Sidefits FitTest <fittest@sidefits.com>\r\n";
    $headers .= "MIME-Version: 1.0 \r\n";
    $headers .= "Content-type: text/html; charset: utf8\r\n";

    // Email Text generieren
    if($lang == "de"){
        $email_subject = "Dein FitTest wartet auf dich!";
        $email_msg = "Hier der Html Text f&uuml;r die Erinnerungsmail 6 Wochen nach zugesendeter Auswertung";
        } else return false;

    // Zeichenanzahl Limitierung Bug Fix
    $email_msg = wordwrap($email_msg, 70);

    // Mail senden
    mail( $email, $email_subject, $email_msg, $headers );

    // In DB Vermerk auf Rem36weekSent im Feld Workstatus
    $sql = "UPDATE fittestresults SET Workstatus = 'Rem6weekSent' WHERE Email = '".$email."'";
    mysqli_query($con, $sql);
}
